package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 1 create a variable=box=container
		/* to create variable we need:
		 * 1. specify dataType
		 * 2. give a name
		 */
		//DataTypes to represent numeric values
		byte box1=127;
		
		short box2=32767;
		
		int box3=327677666; //most used
		
		long box4=405757123456778899L;//will be using
		
		//DataTypes to represent decimals
		
		float price=12.99F;
		
		double price1=13.99; //most used
		
		//to represent a single character
		
		char gender='F';
		
		char sign='$';
		
		//to represent yes or no
		boolean snow=false;
		
		boolean tired=true;
		
		//I want to print 13.99 ???
		System.out.println(price1);
		
		System.out.println("price1");
		
		System.out.println(sign);
				
	}

}
